let fixed=document.querySelector(".header"),introH=document.querySelector(".intro").clientHeight,burger=document.querySelector(".burger"),nav=document.querySelector(".nav");window.addEventListener("scroll",(function(){window.scrollY>introH?fixed.classList.add("fixed"):fixed.classList.remove("fixed")})),burger.addEventListener("click",(function(){burger.classList.toggle("active"),nav.classList.toggle("active"),document.querySelector("body").classList.toggle("lock")}));let navLink=document.querySelectorAll(".nav__link[data-goto]");if(navLink.length>0){function onMenuLinkClick(e){if(e.target.dataset.goto&&document.querySelector(".nav__link.dataset.goto")){let t=document.querySelector(".nav__link.dataset.goto").getBoundingClientRect().top+pageYOffset-document.querySelector("header").offsetHeight;window.scrollTo({top:t,behavior:"smooth"}),e.preventDefault()}}navLink.forEach(e=>{e.addEventListener("click",onMenuLinkClick)})}